import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CountryPage(props) {
    const [country, setCountry] = useState(null);
    const [currency, setCurrency] = useState(null);
    const [convert, setConvert] = useState(0);
    const [formData, setFormData] = useState({comment: '', name: ''});
    const [commentsData, setCommentsData] = useState('loading..');

    useEffect(() => {        
        axios.get(`https://restcountries.eu/rest/v2/name/${props.match.params.name}`).then(countryResponse => {
            const country = countryResponse.data[0];
            setCountry(country);
            displayComments();
            axios.get(`https://api.exchangeratesapi.io/latest?base=USD`).then(currencyResponse => {
                setCurrency(currencyResponse.data.rates[country.currencies[0].code])
            })
        }).catch(() => {
            console.error("failure");
        });
    }, [props.match.params.name]);

    function handleChange(event) {
        setConvert(event.target.value * currency);
    }

    function handleSubmit(event) {
        event.preventDefault();
        axios.post(`http://localhost:8000/comments/`, {...formData, country:props.match.params.name});
    }

    function displayComments() { 
        axios.get(`http://localhost:8000/comments/?countryId=${props.match.params.name}`).then(res => {
            setCommentsData(JSON.stringify(res.data));
        });
    }

    return (
        <React.Fragment>
            {country ? 
            <div>
            <h1>{country.name}</h1>
            <h3>$1 (USD) = {currency} ({country.currencies[0].code})</h3>
            <p>{convert}</p>
            <form><input type="text" onChange={handleChange}/></form>
            {/*<img src={country.flag} alt={country.name} styles="max-width: 100px; max-height: 50px;"></img>*/}


            <form onSubmut={handleSubmit}>
                <input type="text" value={formData.name} onChange={(e) => setFormData({...formData, name:e.target.value })}/>
                <input type="text" value={formData.comment} onChange={(e) => setFormData({...formData, comment:e.target.value })}/>
                <input type="Submit" name="Submit"/>
            </form>
            </div> 

            : <div>loading</div>}
            {commentsData}
        </React.Fragment>
    );
}



export default CountryPage;
