import React, { useState } from 'react';
import axios from 'axios';

const countries = ["39", "49", "380", "90"];

function List() {
    const [flags, setFlags] = useState([]);
    React.useEffect(() => {
        const fetchFlags = async() => {
            const flagResponses = await Promise.all(
                countries.map(country => axios.get(`https://restcountries.eu/rest/v2/callingcode/${country}`))
            ).catch(error => console.log(error));

            const flags = flagResponses.map(flagResponse => flagResponse.data[0]);

            setFlags(flags);
        }
        // axios.get(`https://restcountries.eu/rest/v2/callingcode/${code}`).then(res => {
        //     const flag = res.data[0]
        // }).catch(error => {
        //     console.error("failed to retrieve data", error)
        // })
    }, [countries]);

    return (
        // Cards created using bootstrap https://getbootstrap.com/docs/4.0/components/card/
        <div class="container">
            <div class="card" styles="width: 18rm;">
                <img class="card-img-top" src="https://en.wikipedia.org/wiki/Flag_of_Italy#/media/File:Flag_of_Italy.svg" alt="flag of italy"></img>
                <div class="card-body">
                    <h5 class="card-title">Italy</h5>
                    <p class="card-text">Italy is beautiful</p>
                    <a href="/" class="btn btn-primary">Go somewhere</a>
                </div>
            </div>

            <h1>Germany</h1>
            <h1>Ukraine</h1>
            <h1>Turkey</h1>
        </div>
    );
}

export default List;
