import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Exchange() {

    const [currencies, setCurrencies] = useState({});
    const [load, setLoad] = useState(true)
    const [tag, setTag] = useState('');
    const [apiResponse, setApiResponse] = useState({});

    useEffect(() => {
        if(apiResponse == {}) return;
        axios.get(`https://api.exchangeratesapi.io/latest?symbols=USD,${tag}`).then(res => {
            setCurrencies(res.data.rates)
            console.log("here", res.data.rates)
            setLoad(false)

        })

    }, [apiResponse]);

    function handleChange(event) {
        setTag(event.target.value)
    }

    function handleSubmit(event) {
        event.preventDefault();
        setApiResponse({"value":tag});
    }

    function LoadList() {
        return (
        <div>
            <p>{tag}: {currencies[`${tag}`]}</p>
        </div>
        );
    }

    return (
        <div>
            <form onSubmit={handleSubmit}> 
                <input type="text" value={tag} onChange={handleChange} />
                <input type="submit" />
            </form>
            {load ? "Enter a currency tag" : <p>{LoadList()}</p>}
        </div>
    );
}


export default Exchange;
