import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Exchange() {

    const [currencies, setCurrencies] = useState({});
    const [load, setLoad] = useState(true)

    useEffect(() => {
        axios.get("https://api.exchangeratesapi.io/latest?base=USD").then(res => {

            setCurrencies(res.data.rates)
            console.log("here", res.data.rates)
            setLoad(false)

        })

    }, []);

    function LoadList() {
        return (
        <div>
            <p>AUD: {currencies.AUD}</p>
            <p>BGN: {currencies.BGN}</p>
            <p>BRL: {currencies.BRL}</p>
            <p>CAD: {currencies.CAD}</p>
            <p>CHF: {currencies.CHF}</p>
            <p>CNY: {currencies.CNY}</p>
            <p>CZK: {currencies.CZK}</p>
            <p>DKK: {currencies.DKK}</p>
            <p>GDP: {currencies.GBP}</p>
        </div>
        );
    }

    return (
        <div>
            {load ? "loading" : <p>{LoadList()}</p>}
        </div>
    );
}


export default Exchange;
