import React, { useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, NavLink } from "react-router-dom";

const countries = ["39", "49", "86", "90"];

function List() {
    const [flags, setFlags] = useState(null);
    React.useEffect(() => {
        const fetchFlags = async() => {
            const flagResponses = await Promise.all(
                countries.map(country => axios.get(`https://restcountries.eu/rest/v2/callingcode/${country}`))
            ).catch(error => console.log(error));

            const flags = flagResponses.map(flagResponse => flagResponse.data[0]);

            setFlags(flags);
        };
        fetchFlags();
        // axios.get(`https://restcountries.eu/rest/v2/callingcode/${code}`).then(res => {
        //     const flag = res.data[0]
        // }).catch(error => {
        //     console.error("failed to retrieve data", error)
        // })
    }, [countries]);

    return (
        // Cards created using bootstrap https://getbootstrap.com/docs/4.0/components/card/
        <React.Fragment>
            <div class="container">
            {flags ? flags.map(flag => (
            <div>
                <div className = "row d-flex justify-content-center">
                    <div className = "col-6 card" style={{margin:"5vh 0px"}}>
                        <img class="card-img-top" src={flag.flag} alt="flag of italy"></img>
                        <div class="card-body">
                            <h5 class="card-title">{flag.name}</h5>
                            <p class="card-text">Italy is beautiful</p>
                            <NavLink to={`/${flag.name}`} class="btn btn-primary">Info</NavLink>
                        </div>
                    </div>
                </div>
            </div>
            )) : null}          
            </div>
        </React.Fragment>
    );
}

export default List;
