import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CountryPage(props) {
    const [country, setCountry] = useState(null);
    const [currency, setCurrency] = useState([]);

    useEffect(() => {        
        axios.get(`https://restcountries.eu/rest/v2/name/${props.match.params.name}`).then(res => {
            setCountry(res.data[0])
            //console.log(res.data[0]);
            //console.log(res.data[0].currencies[0].code);
            axios.get(`https://api.exchangeratesapi.io/latest?base=USD`).then(res => {
                console.log("here1", country.currencies[0].code)
                console.log(res)
                console.log("here", res.data.rates[country.currencies[0].code])
                setCurrency(res.data.rates);

            })
        }).catch(() => {
            console.error("failure");
        });
    }, [props.match.params.name]);


    if(country != null) console.log(country.currencies[0].code);

    return (
        <React.Fragment>
            {country ? <div>{country.name} </div> : <div>loading</div>}
        </React.Fragment>
    );
}



export default CountryPage;
