import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Exchange() {

    const [currencies, setCurrencies] = useState({});
    const [load, setLoad] = useState(true)
    var userCurrency = "TRY"

    useEffect(() => {
        axios.get(`https://api.exchangeratesapi.io/latest?symbols=USD,${userCurrency}`).then(res => {
            setCurrencies(res.data.rates)
            console.log("here", res.data.rates)
            setLoad(false)

        })

    }, []);

    function handleChange() {

    }

    function LoadList() {
        return (
        <div>
            <p>{userCurrency}: {currencies[`${userCurrency}`]}</p>
        </div>
        );
    }

    return (
        <div>
            <input type="text" onSubmit={handleChange}></input>
            {load ? "loading" : <p>{LoadList()}</p>}
        </div>
    );
}


export default Exchange;
