import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CountryPage(props) {
    const [test, setTest] = useState(null);


    React.useEffect(() => {        
        axios.get(`https://restcountries.eu/rest/v2/name/${props.match.params.name}`).then(res => {
            setTest(res.data[0]);
        });
    }, [props.match.params.name]);

    console.log(test);



    return (
        <React.Fragment>
            {test ? <div>{test.name}</div> : <div>loading</div>}
        </React.Fragment>
    );
}



export default CountryPage;
