import React, { useState } from 'react';
import axios from 'axios';


function List() {
    const countryList = ["39", "49", "380", "90"];
    const [flagList, setFlagList] = useState([]);
    let code = 0;

    React.useEffect(() => {
        code = countryList[flagList.length];
        axios.get(`https://restcountries.eu/rest/v2/callingcode/1`).then(res => {
            console.log(res.data);
            if (flagList.length < 4) {
                setFlagList(...flagList, res.data)
            }
        }).catch(() => {
            console.error("failed to retrieve data")
        })
    });

    return (
        // Cards created using bootstrap https://getbootstrap.com/docs/4.0/components/card/
        <div class="container">
            <div class="card" styles="width: 18rm;">
                <img class="card-img-top" src="https://en.wikipedia.org/wiki/Flag_of_Italy#/media/File:Flag_of_Italy.svg" alt="flag of italy"></img>
                <div class="card-body">
                    <h5 class="card-title">Italy</h5>
                    <p class="card-text">Italy is beautiful</p>
                    <a href="/" class="btn btn-primary">Go somewhere</a>
                </div>
            </div>

            <h1>Germany</h1>
            <h1>Ukraine</h1>
            <h1>Turkey</h1>
        </div>
    );
}

export default List;
